import React, { useEffect, useState } from 'react';
import VideoTile from './VideoTile';
import { API_KEY } from "./data";
import { Link } from 'react-router-dom';
import mb from '../assets/mb.jpg';

const HomeVideos = ({ Category }) => {
  const [data, setData] = useState([]);

  const fetchdata = async () => {
    const videoList_url = `https://youtube.googleapis.com/youtube/v3/videos?part=snippet%2CcontentDetails%2Cstatistics&chart=mostPopular&regionCode=US&key=${API_KEY}`;
    await fetch(videoList_url)
      .then(response => response.json())
      .then(data => {
        if (data.items) {
          setData(data.items);
        } else {
          setData([]); // Handle case when data.items is undefined
        }
      })
      .catch(error => {
        console.error('Error fetching data:', error);
        setData([]); // Handle error case
      });
  };

  useEffect(() => {
    fetchdata();
  }, [Category]);

  return (
    <div className='grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4'>
      {data.length > 0 ? (
        data.map(video => (
          <Link key={video.id} to={`video/${video.id}`}>
            <VideoTile
              thumbnail={video.snippet?.thumbnails?.medium?.url || mb}
              channelImg={mb} // Replace with actual channel image URL if available
              description={video.snippet?.description || 'No description available'}
              channelName={video.snippet?.channelTitle || 'Unknown channel'}
              views={video.statistics?.viewCount || '0'}
              lastTime={new Date(video.snippet?.publishedAt).toLocaleDateString()}
              videoLength={video.contentDetails?.duration || 'Unknown'}
            />
          </Link>
        ))
      ) : (
        <p>Loading videos...</p>
      )}
    </div>
  );
};

export default HomeVideos;
