import React, { useEffect, useState } from 'react';
import VideoTile from './VideoTile';
import { API_KEY } from './data';
import { Link } from 'react-router-dom';
import mb from '../assets/mb.jpg';

const HomeVideos = ({ Category }) => {
  const [data, setData] = useState([]);

  const fetchdata = async () => {
    const videoList_url = `https://youtube.googleapis.com/youtube/v3/videos?part=snippet%2CcontentDetails%2Cstatistics&chart=mostPopular&maxResults=20&regionCode=US&key=${API_KEY}`;
    await fetch(videoList_url)
      .then((response) => response.json())
      .then((data) => {
        if (data.items) {
          setData(data.items);
        } else {
          setData([]); // Handle case when data.items is undefined
        }
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
        setData([]); // Handle error case
      });
  };

  useEffect(() => {
    fetchdata();
  }, [Category]);

  return (
    <div className='grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4'>
      {data.map((item, index) => {
        return (
          <Link key={item.id} to={`video/${item.snippet.categoryId}/${item.id}`}>
            <VideoTile
              thumbnail={item.snippet.thumbnails.medium.url}
              channelImg={mb}
              description={item.snippet.description}
              channelName={item.snippet.channelTitle}
              views={item.statistics.viewCount}
              lastTime={new Date(item.snippet.publishedAt).toLocaleDateString()}
              videoLength={item.contentDetails.duration}
            />
          </Link>
        );
      })}
    </div>
  );
};

export default HomeVideos;
